package net.jeeshop.biz.cms.service;import net.jeeshop.biz.base.bean.PageBean;import net.jeeshop.biz.base.bean.PageQueryBean;import net.jeeshop.biz.base.client.BaseMapper;import net.jeeshop.biz.base.service.BaseService;import net.jeeshop.biz.cms.client.HotQueryMapper;import net.jeeshop.biz.cms.model.HotQuery;import net.jeeshop.biz.cms.model.HotQueryExample;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class HotQueryService extends BaseService<HotQuery, HotQueryExample> {	 @Autowired	 private HotQueryMapper hotQueryMapper;	/* 	 * @return 	 */	@Override	protected BaseMapper<HotQuery, HotQueryExample> getMapper() {		return hotQueryMapper;	}	/**	 * @param pageQueryBean	 * @return	 */	public PageBean<HotQuery> selectPageBean(final HotQueryExample example,			PageQueryBean pageQueryBean) {		return executePageQuery(new PageQueryExecutor<HotQuery>(){			@Override			public List<HotQuery> executeQuery() {				return hotQueryMapper.selectByExample(example);			}		},pageQueryBean);	}}