package net.jeeshop.biz.cms.service;import java.util.List;import net.jeeshop.biz.base.bean.PageBean;import net.jeeshop.biz.base.bean.PageQueryBean;import net.jeeshop.biz.base.client.BaseMapper;import net.jeeshop.biz.base.service.BaseService;import net.jeeshop.biz.cms.client.HotqueryMapper;import net.jeeshop.biz.cms.model.Hotquery;import net.jeeshop.biz.cms.model.HotqueryExample;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;@Servicepublic class HotqueryService extends BaseService<Hotquery, HotqueryExample> {	 @Autowired	 private HotqueryMapper hotqueryMapper;	/* 	 * @return 	 */	@Override	protected BaseMapper<Hotquery, HotqueryExample> getMapper() {		return hotqueryMapper;	}	/**	 * @param queryParams	 * @param pageQueryBean	 * @return	 */	public PageBean<Hotquery> selectPageBean(final HotqueryExample example,			PageQueryBean pageQueryBean) {		return executePageQuery(new PageQueryExecutor<Hotquery>(){			@Override			public List<Hotquery> executeQuery() {				return hotqueryMapper.selectByExample(example);			}		},pageQueryBean);	}}